import * as React from 'react';
import LeftNavBarMenu from "./LeftNavBarMenu";
import SettingsIcon from "../../icons/SettingsIcon";
import styled from "../../../utils/styled";

interface LeftNavBarProps {
    title: string;
    squadrons?: string[];
}

const LeftNavBar: React.FC<LeftNavBarProps> = ({title}) => (
    <Wrapper>
        <LeftNavBarTitle>
            <Title
            className="shine">{title}</Title>
        </LeftNavBarTitle>
    <LeftNavBarMenu menuTitle={"Squadron"} rows={['10IS','30IS']}/>
        <NavBorder>
        </NavBorder>
        <AppButtonSection>
        </AppButtonSection>
    </Wrapper>
);

export default LeftNavBar;

const AppButtonSection = styled('div')`
display: flex;
align-items: center;
position: fixed;
bottom: 0;
height: 45px;
padding-left: 5px;
`;

const Wrapper = styled('div')`
    position: fixed;
    display: block;
    width: 198px;
    height: 100vh;
    background-color: ${props => props.theme.colors.MenuBackground};
    color: ${props => props.theme.colors.brand};
    font-family: ${props => props.theme.fonts.headings};
    z-index: 100;
`;

const LeftNavBarTitle = styled('div')`
    margin-left: 0;


html {
background-color: #333;
text-align: center
}

body {
padding-top: 3em;
}

.shine, .chrome {
font-size: 3em;
margin: 0 auto;
margin-bottom: 1em;
font-weight: bold;
}

.shine {
background: #222 -webkit-gradient(linear, left top, right top, from(#222), to(#222), color-stop(0.5, #fff)) 0 0 no-repeat;
-webkit-background-size: 150px;
color: rgba(255, 255, 255, 0.3);
-webkit-background-clip: text;
-webkit-animation-name: shine;
-webkit-animation-duration: 5s;
-webkit-animation-iteration-count: infinite;
text-shadow: 0 0px 0px rgba(255, 255, 255, 0.5);
}

.chrome {
background: #222 -webkit-linear-gradient(-40deg, transparent 0%, transparent 40%, #fff 50%, transparent 60%, transparent 100%) no-repeat 0 0;
-webkit-background-size: 200px;
color: rgba(255, 255, 255, 0.3);
-webkit-background-clip: text;
-webkit-animation-name: shine;
-webkit-animation-duration: 5s;
-webkit-animation-iteration-count: infinite;
text-shadow: 0 0px 0px rgba(255, 255, 255, 0.5);
}

`;

const Title = styled('h2')`
    margin: 10px;
    font-weight: 500;
`;

const NavBorder = styled('div')`
position: absolute;
width: 1px;
height: 100%;
left: 198px;
top: 0px;
background: #949494;
/* Borders */

box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.25);

`;
