export async function callApi(method: string, path: string, data?: any) {
    const res = await fetch(`/${path}`, {
        method,
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
    });
    return res.json();
}
