import * as React from 'react';
import {NavLink} from 'react-router-dom';
import styled from '../../utils/styled';
import Container from "./Container";
import SettingsIcon from "../icons/SettingsIcon";
import {reverseSpin, spin} from "../animations/transitions";
import HeaderMenu from "./HeaderMenu";
import MenuTitleBar from "../icons/MenuTitleBar";

interface HeaderProps {
    title: string;
    squadrons?: string[];
}

const NavMenu: React.FC<HeaderProps> = ({title}) => (
    <Wrapper>
        {/*<TopBorder>*/}
        {/*</TopBorder>*/}
        <HeaderTitle>
            <Title>{title}</Title>
        </HeaderTitle>
        <SettingsIcon/>
        <MenuTitleBar/>
        <NavBorder>
        </NavBorder>
        <HeaderNav>
            {/*<HeaderNavLink exact to="/" activeClassName="is-active">*/}
            {/*    Home*/}
            {/*</HeaderNavLink>*/}
            {/*<HeaderNavLink to="/members" activeClassName="is-active">*/}
            {/*    Members*/}
            {/*</HeaderNavLink>*/}
        </HeaderNav>

    </Wrapper>
);

export default NavMenu;


const Wrapper = styled('div')`
    display: flex;
    width: 198px;
    height: 100%;
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.brand};
    font-family: ${props => props.theme.fonts.headings};
    position: fixed;
    z-index: 100;


    .MenuTitleBar {
    width: 199px;
    position: absolute;
    }

`;

const HeaderTitle = styled('div')`
    margin-left: 0;
`;

const HeaderNav = styled('nav')`
    flex: 1 1 auto;
    flex-direction: column;

`;

const HeaderNavLink = styled(NavLink)`
    margin: 0 1rem;
    &.is-active {
        text-decoration: underline;
    }
`;

const Title = styled('h2')`
    margin: 10px;
    font-weight: 500;
`;

const NavBorder = styled('div')`
position: absolute;
width: 1px;
height: 100%;
left: 198px;
top: 0px;
background: #949494;
/* Borders */

box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.25);

`;

const TopBorder = styled('div')`
position: absolute;
width: 100vw;
height: 1px;
left: 198px;
top: 55.5px;
background: #949494;

/* Borders */

box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.25);
`;
