import * as React from 'react';
import GlassBallIcon from "../../icons/GlassBall";
import MenuTitleBar from "../../icons/MenuTitleBar";
import styled from "../../../utils/styled";

interface LeftNavBarMenuProps {
    menuTitle: string;
    rows: string[];
}

const LeftNavBarMenu: React.FC<LeftNavBarMenuProps> = ({children, menuTitle, rows}) => (
    <Wrapper>
        <Menu>
            <MenuTitle>
                <span>{menuTitle}</span>
                <MenuTitleBar/>
            </MenuTitle>
            <Menu>
                {rows.map(squadron => (
                            <MenuRow>
                                <a>{item}</a>
                            </MenuRow>
                ))}
            </Menu>
            <MenuActionBall>
                <GlassBallIcon/>
            </MenuActionBall>
        </Menu>
    </Wrapper>
);

export default LeftNavBarMenu;

const MenuRow = styled('div')`
text-align: center;
cursor: pointer;
color: rgba(255, 255, 255, 0.3);
line-height: 22px;
font-size: 16px;
width: 100%;
display: flex;
padding-left: 20px;

:hover {
background: #333;
}

`;

const Wrapper = styled('div')`
display: block;
width: 100%;
  font-family: ${props => props.theme.fonts.headings};
  font-size: large;
`;

const Menu = styled('div')`
width: inherit;
`;

const MenuActionBall = styled('div')`
cursor: pointer;
position: absolute;
right: 6px;
top: 50px;
`;

const MenuTitle = styled('div')`
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;

span {
    width: 90%;
    height: 24px;
    position: absolute;
}

`;
