import { all, call, fork, put, takeEvery } from 'redux-saga/effects';
import { MembersActionTypes } from './types';
import { fetchError, fetchSuccess } from './actions';
import { callApi } from '../../utils/api';

const API_ENDPOINT = 'https://turbine.dev.dev.east.paas.geointservices.io/';

function *handleFetch() {
    try {
        // To call async functions, use redux-saga's `call()`.
        const res = yield call(callApi, 'get', API_ENDPOINT, 'api/members');

        if (res.error) {
            console.log("error :" + res.error);
            yield put(fetchError(res.error));
        } else {
            console.log("data returned: " + res);
            yield put(fetchSuccess(res));
        }
    } catch (err) {
        if (err instanceof Error && err.stack) {
            console.log(" err stack :" + err.stack);
            yield put(fetchError(err.stack));
        } else {
            console.log("dispatching fetcherror");
            yield put(fetchError('An unknown error occured.'));
        }
    }
}

// This is our watcher function. We use `take*()` functions to watch Redux for a specific action
// type, and run our saga, for example the `handleFetch()` saga above.
function *watchFetchRequest() {
    yield takeEvery(MembersActionTypes.FETCH_REQUEST, handleFetch);
}

// We can also use `fork()` here to split our saga into multiple watchers.
function *membersSaga() {
    yield all([fork(watchFetchRequest)]);
}

export default membersSaga;
