import * as React from 'react';
import {NavLink} from 'react-router-dom';
import styled from '../../utils/styled';
import Container from "./Container";
import icon_Settings from "../icons/icon_Settings";
import SettingsIcon from "../icons/icon_Settings";
interface HeaderProps {
    title: string;
}

const Wrapper = styled('header')`

    width: 198px;
    height: 100vh;
    background-color: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.brand};
    font-family: ${props => props.theme.fonts.headings};
    position: fixed;
    z-index: 100;

    .svg {
    position: absolute;
    top: 10px;
    left: 10px;
    }
`;

const HeaderInner = styled(Container)`
    display: flex;
    flex-direction: column;
    justify-content: space-between;

`;

const HeaderTitle = styled('div')`
    margin-left: 0;
`;

const HeaderNav = styled('nav')`
    flex: 1 1 auto;
    flex-direction: column;

`;

const HeaderNavLink = styled(NavLink)`
    margin: 0 1rem;
    &.is-active {
        text-decoration: underline;
    }
`;

const Title = styled('h2')`
    margin: 10px;
    font-weight: 500;
`;

const NavBorder = styled('div')`
position: absolute;
width: 1px;
height: 100%;
left: 198px;
top: 0px;
background: #949494;
/* Borders */

box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.25);

`;

const TopBorder = styled('div')`
position: absolute;
width: 100vw;
height: 1px;
left: 198px;
top: 55.5px;
background: #949494;

/* Borders */

box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.25);
`;



const NavMenu: React.FC<HeaderProps> = ({ title }) => (
    <Wrapper>
        <HeaderInner>
            <TopBorder>
            </TopBorder>
            <HeaderTitle>
                <Title>{title}</Title>
            </HeaderTitle>
            <NavBorder>
            </NavBorder>
            <HeaderNav>
                {/*<HeaderNavLink exact to="/" activeClassName="is-active">*/}
                {/*    Home*/}
                {/*</HeaderNavLink>*/}
                {/*<HeaderNavLink to="/members" activeClassName="is-active">*/}
                {/*    Members*/}
                {/*</HeaderNavLink>*/}
            </HeaderNav>
            <SettingsIcon/>
        </HeaderInner>
    </Wrapper>
);

export default NavMenu;
