import * as React from 'react';
import styled from "../../../utils/styled";
import {connect} from "react-redux";
import {
    showSquadronInput,
    squadronPostRequest,
    squadronsFetchRequest,
} from "../../../store/squadrons/actions";
import {ApplicationState} from "../../../store";
import SquadronModel from "../../../store/squadrons/SquadronModel";
import {useState} from "react";
import {postNewSquadron} from "../../../store/squadrons/sagas";
import {EaseIn} from "../../animations/transitions";

interface Props {
}

interface PropsFromDispatch {
    fetchSquadrons: typeof squadronsFetchRequest;
    showInput: typeof showSquadronInput;
}

type AllProps = Props & PropsFromDispatch;

const AddSquadronBar: React.FC<AllProps> = props => {
    const [newSqName, updateSqName] = useState("");
    const [newSqPAS, updatePAS] = useState("");

    async function handleClick() {
        await postNewSquadron(new SquadronModel(newSqName, newSqPAS));
        await props.fetchSquadrons();
        props.showInput();
    }

    return (
        <Wrapper>
            <InputGrp>
            <input
                onChange={(e: any) => updateSqName(e.target.value)}
                className={"SquadronInput"}
                placeholder={"Name"}>
            </input>
            <input
                onChange={(e: any) => updatePAS(e.target.value)}
                className={"PASInput"}
                placeholder={"PAS Code"}>
            </input>
            </InputGrp>
            <div
                className={"btn-box"}>
                    <button
                        className={"SaveBtn btn btn-white btn-animation-1"}
                        onClick={() => {
                            handleClick()
                        }}
                    >
                        SAVE
                    </button>

            </div>
        </Wrapper>
    )
};

const mapStateToProps = ({squadrons}: ApplicationState) => ({
    newSquadron: squadrons.newInput,
    post: squadrons.posting,
});


const mapDispatchToProps = {
    postNewSquadron: squadronPostRequest,
    fetchSquadrons: squadronsFetchRequest,
    showInput: showSquadronInput,
};

export const StyledAddSquadronBar = connect(
    mapStateToProps,
    mapDispatchToProps,
)(AddSquadronBar);

const Wrapper = styled('div')`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 100%;
  font-family: ${props => props.theme.fonts.headings};
  font-size: 16px;
  -webkit-animation: ${EaseIn} .4s;
  transition: all .4s;
  margin-top: 5px;

  .SquadronInput {
  text-align: center;
  outline: none;
  width: 76px;
  height: 20px;
  margin-left: 4px;
  background: none;
  border: solid 1px rgba(211,211,211,0.55);
  border-radius: 4px;
  color: rgba(211,211,211,0.89);
  }

  .PASInput {
  text-align: center;
  outline: none;
   width: 110px;
  height: 20px;
   background: none;
  border: solid 1px ${props => props.theme.colors.brand};
  border-radius: 4px;
  color: #d3d3d3;
  }

  .SaveBtn {
  background: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.brand};
  text-align: center;
  outline: none;
  cursor: pointer;
  width: 80%;
  height: 25px;
  margin: 10px;
  border: none;
  border-radius: 4px;
  transition: all 0.3s ease 0s;

  :hover{
  background: ${props => props.theme.colors.textLight}
  }
  }

  .btn-box {
    display: flex;
  flex-direction: row;
  justify-content: space-around;
  width: 100%;
  }
`;

const InputGrp = styled('div')`
 display: flex;
  flex-direction: row;
  justify-content: space-around;
  width: 100%;
`;
