import * as React from 'react';
import styled from '../../utils/styled';
import MenuTitleBar from "../icons/MenuTitleBar";
import LoadingSpinner from "../data/LoadingSpinner";
import GlassBallIcon from "../icons/GlassBall";

interface LeftNavMenuProps {
    items: any[];
    loading: boolean;
    menuTitle: string;
}

const LeftNavMenu: React.FC<LeftNavMenuProps> = ({ items,  menuTitle,loading,}) => (
    <Menu>
        <MenuTitle>
            <span>{menuTitle}</span>
            <MenuTitleBar/>
        </MenuTitle>
        {loading && (
            <LoadingSpinner />
        )}
        <Menu>
            {items.map((item, index) => (
                <MenuRow
                    key={index}>
                    <span>{items[0].toString}</span>
                </MenuRow>
            ))}
        </Menu>
        <MenuActionBall>
            <GlassBallIcon/>
        </MenuActionBall>
    </Menu>
);

export default LeftNavMenu;

const MenuRow = styled('div')`
text-align: center;
cursor: pointer;
color: rgba(255, 255, 255, 0.3);
line-height: 22px;
font-size: 16px;
width: 100%;
display: flex;
padding-left: 20px;

:hover {
background: #333;
}

`;

const MenuWrapper = styled('div')`
display: block;
width: 100%;
  font-family: ${props => props.theme.fonts.headings};
  font-size: large;
`;

const Menu = styled('div')`
width: inherit;
`;

const MenuActionBall = styled('div')`
cursor: pointer;
position: absolute;
right: 6px;
top: 50px;
`;

const MenuTitle = styled('div')`
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;

span {
    width: 90%;
    height: 24px;
    position: absolute;
}

`;
