import * as React from 'react';
import styled from "../../../utils/styled";
import {connect} from "react-redux";
import {updateSquadronInputState} from "../../../store/squadrons/actions";
import {ApplicationState} from "../../../store";
import SquadronModel from "../../../store/squadrons/SquadronModel";
import {useState} from "react";


interface PropsFromDispatch {
    updateSquadronInputState: typeof updateSquadronInputState;
}

// Combine both state + dispatch props - as well as any props we want to pass - in a union type.
type AllProps = PropsFromDispatch;

class AddSquadronBar extends React.Component<AllProps> {
    // constructor(props: any) {
    //     super(props);
    //     this.state = {
    //         newSquadron: "",
    //         newPASCode: ""
    //     }
    // }
    handleClick = (newSquadron: string ,newPASCode: string) => {
        this.props.updateSquadronInputState(new SquadronModel(newSquadron,newPASCode))
    };
    public render() {
        const {updateSquadronInputState: updateInputState} = this.props;


        const [newSquadron, setNewSquadron] = useState("");
        const [newPASCode, setNewPASCode] = useState("");

        return (
            <Wrapper>
                <input
                    onChange={(e: any) => setNewSquadron(newSquadron + e.target.value)}
                    className={"SquadronInput"}
                placeholder={"Name"}>
                </input>
                <input
                    onChange={(e: any) => setNewPASCode(newPASCode + e.target.value)}
                    className={"PASInput"}
                    placeholder={"PAS Code"}>
                </input>
                <button
                onClick={() => this.handleClick(newSquadron, newPASCode)}>
                    save
                </button>
            </Wrapper>
        );
    }
}

const mapStateToProps = ({squadrons}: ApplicationState) => ({
    Input: squadrons.newInput,
    post: squadrons.posting,
});


const mapDispatchToProps = {
    updateSquadronInputState,
};

export default connect(
    mapStateToProps,
    mapDispatchToProps,
)(AddSquadronBar);

const Wrapper = styled('div')`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  width: 100%;
  font-family: ${props => props.theme.fonts.headings};
  font-size: 16px;
  margin-top: 5px;
  margin-bottom: 5px;

  .SquadronInput {
  width: 60px;
  height: 20px;
  background: none;
  border: solid 1px white;
  border-radius: 4px;
  color: white;
  }

  .PASInput {
   width: 120px;
  height: 20px;
   background: none;
  border: solid 1px white;
  border-radius: 4px;
  color: white;
  ;
  }

`;
