import * as React from 'react';
import styled from "../../../utils/styled";
import {connect} from "react-redux";
import {
    showSquadronInput,
    squadronPostRequest,
    squadronsFetchRequest,
} from "../../../store/squadrons/actions";
import {ApplicationState} from "../../../store";
import SquadronModel from "../../../store/squadrons/SquadronModel";
import {useState} from "react";
import {postNewSquadron} from "../../../store/squadrons/sagas";

interface Props {
}

interface PropsFromDispatch {
    fetchSquadrons: typeof squadronsFetchRequest;
    showInput: typeof showSquadronInput;
}

type AllProps = Props & PropsFromDispatch;

const AddSquadronBar: React.FC<AllProps> = props => {
    const [newSqName, updateSqName] = useState("");
    const [newSqPAS, updatePAS] = useState("");

    async function handleClick() {
        await postNewSquadron(new SquadronModel(newSqName, newSqPAS));
        await props.fetchSquadrons();
        props.showInput();
    }

    return (
        <Wrapper>
            <input
                onChange={(e: any) => updateSqName(e.target.value)}
                className={"SquadronInput"}
                placeholder={"Name"}>
            </input>
            <input
                onChange={(e: any) => updatePAS(e.target.value)}
                className={"PASInput"}
                placeholder={"PAS Code"}>
            </input>
            <div
                className={"box"}>
                <a href="#" className={"btn btn-white btn-animation-1"}>
                    <button
                        className={"SaveBtn"}
                        onClick={() => {
                            handleClick()
                        }}
                    >
                        SAVE
                    </button>

                </a>
            </div>
        </Wrapper>
    )
};

const mapStateToProps = ({squadrons}: ApplicationState) => ({
    newSquadron: squadrons.newInput,
    post: squadrons.posting,
});


const mapDispatchToProps = {
    postNewSquadron: squadronPostRequest,
    fetchSquadrons: squadronsFetchRequest,
    showInput: showSquadronInput,
};

export const StyledAddSquadronBar = connect(
    mapStateToProps,
    mapDispatchToProps,
)(AddSquadronBar);

const Wrapper = styled('div')`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 100%;
  font-family: ${props => props.theme.fonts.headings};
  font-size: 16px;
  margin-top: 5px;
  margin-bottom: 5px;

  .SquadronInput {
  width: 60px;
  height: 20px;
  background: none;
  border: solid 1px white;
  border-radius: 4px;
  color: white;
  }

  .PASInput {
   width: 120px;
  height: 20px;
   background: none;
  border: solid 1px white;
  border-radius: 4px;
  color: white;
  }

  .SaveBtn {
  color: darkgray;
  outline: none;
  cursor: pointer;
  width: 50px;
  height: 25px;
  border: none;
   background: none;
   border-radius: 50%;
  }

 body{
  //background:lightblue;
  text-align:center;
  box-sizing:border-box;
  font-family:"Lato",Sans-serif;
/*   position:relative; */
}

.box{
  position: relative;
  top:50%;
  left:50%;

}

//.a{
//height: 100%;
//width: 100%;
//}

.btn:link,
.btn:visited{
  text-decoration: none;
  text-transform:uppercase;
  position:relative;
  top:0;
  left:0;
  padding:2px 4px;
  border-radius:100px;
  display:inline-block;
  transition: all .5s;
}

.btn-white{
font-size: 16px;
  color: ${props => props.theme.colors.brand};
}

.btn:hover{
   box-shadow:0px 10px 10px rgba(0,0,0,0.2);
   transform : translateY(-3px);
}

.btn:active{
  box-shadow:0px 5px 10px rgba(0,0,0,0.2)
  transform:translateY(-1px);
}

.btn-bottom-animation-1{
  animation:comeFromBottom 1s ease-out .8s;
}

.btn::after{
  content:"";
  text-decoration: none;
  text-transform:uppercase;
  position:absolute;
  width:100%;
  height:100%;
  top:0;
  left:0;
  border-radius:100px;
  display:inline-block;
  z-index:-1;
  transition: all .5s;
}

.btn-white::after {
    background: ${props => props.theme.colors.background};
}

.btn-animation-1:hover::after {
    transform: scaleX(1.4) scaleY(1.6);
    opacity: 0;
}

@keyframes comeFromBottom{
  0%{
    opacity:0;
    transform:translateY(40px);
  } 
  100%{
    opacity:1;
    transform:translateY(0);
  }
}
`;
